import React, { Component } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class FilterForm extends Component  {

    searchInput = React.createRef();

    searchHandler = (e) => {
        e.preventDefault();
        this.props.searchQuery(this.searchInput.current.value);
        e.currentTarget.reset();
    }

    render () {
        return (
            <Form>
                <Row>
                    <Col xs={6} md={4} lg={4}>
                      <Form.Control onChange={this.searchHandler} ref={this.searchInput} className="custom" placeholder="Search" />
                    </Col>
                    <Col xs={6} md={4} lg={4}>
                    <Form.Control as="select" className="custom">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </Form.Control>
                    </Col>
                    <Col xs={12} md={4} lg={{ span: 3, offset: 1 }}>
                        <Button>CREATE NEW ROLE</Button>
                    </Col>
                </Row>
            </Form>
        )
    }
}

export default FilterForm;
