import React from 'react';
import Col from 'react-bootstrap/Col';
import FilterForm from './FilterForm';

const Header = ({searchQuery, filterQuery, addRole, showForm}) => {

    toggleForm = () => {
        this.setState( prevState => {
            return {
                showForm: !prevState.showForm
            };
        });
    }

    return (
        <header>
            <Col>
                <h1>User Roles Management</h1>
            </Col>
            <FilterForm showForm={showForm} addRole={addRole} filterQuery={filterQuery} searchQuery={searchQuery} />
            <button onClick={this.toggleForm}>CREATE NEW ROLE</button>
        </header>

    )
}

export default Header;
