import React, { Component } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class FilterForm extends Component  {

    searchInput = React.createRef();
    filterInput = React.createRef();

    searchHandler = () => {
        this.props.searchQuery(this.searchInput.current.value);
    }

    filterHandler = () => {
        this.props.searchQuery(this.searchInput.current.value);
    }

    render () {
        return (
            <Form>
                <Row>
                    <Col xs={6} md={4} lg={4}>
                      <Form.Control onChange={this.searchHandler} ref={this.searchInput} className="custom" placeholder="Look up name..." />
                    </Col>
                    <Col xs={6} md={4} lg={4}>
                    <Form.Control ref={this.filterInput} as="select" className="custom">
                        <option>Search status..</option>
                        <option value="true">Active</option>
                        <option value="false">Inactive</option>
                        <option>Active and Inactive</option>
                    </Form.Control>
                    </Col>
                    <Col xs={12} md={4} lg={{ span: 3, offset: 1 }}>
                        <Button>CREATE NEW ROLE</Button>
                    </Col>
                </Row>
            </Form>
        )
    }
}

export default FilterForm;
